import { Button } from './componentes/Button';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';


function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if(window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  window.addEventListener('resize', showButton);
  return (
    <>
        <nav className='navbar'>
            <div className='navbar-container'>
                <Link to="/" className="navbar-logo">
                  NUAR <i className='fab fa-typo3'></i>
                </Link>
                <div className='menu-icon' onClick={handleClick}>
                  <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                  <div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                      <li className='nav-item'>
                        <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                          Home
                        </Link>
                      </li>
                      <li className='nav-item'>
                        <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
                          Serviços
                        </Link>
                      </li>
                      <li className='nav-item'>
                        <Link to='/promotions' className='nav-links' onClick={closeMobileMenu}>
                          Promoções
                        </Link>
                      </li>
                      <li className='nav-item'>
                        <Link to='/order' className='nav-links' onClick={closeMobileMenu}>
                          Pedido
                        </Link>
                      </li>
                    </ul>
                    {button && <Button butonStyle='btn--outline'>SING UP</Button>}
                  </div>
                </div>
            </div>
        </nav>
    </>
  );
}

export default Navbar;
